mapper.select:
SELECT B.* FROM( SELECT ROWNUM RNUM, A.* FROM(
SELECT LEVEL, BO_NO, BOARD_TYPE, LPAD(' re:', 12*(level-1), '&nbsp;&nbsp;') || BO_TITLE AS BO_TITLE, BO_WRITER, BO_HIT, BO_LIKE, TO_CHAR(BO_DATE, 'YYYY-MM-DD HH:MI') BO_DATE FROM BOARD2
START WITH BO_PARENT IS NULL CONNECT BY PRIOR BO_NO = BO_PARENT ORDER SIBLINGS BY BO_NO DESC ) A ) B WHERE RNUM BETWEEN #{startRow} AND #{endRow}

mapper.update:
UPDATE BOARD2
SET BO_HIT = BO_HIT + 1
WHERE BO_NO = #{bo_no}

mapper.insert:

	SELECT NVL(MAX(BO_NO), 0)+1 FROM BOARD2

INSERT INTO BOARD2 (
    BO_NO,    BOARD_TYPE,    BO_TITLE,
    BO_WRITER,    BO_DATE,    BO_CONTENT,
    BO_PASS,    BO_IP

    ,  BO_PARENT

) VALUES (
    #{bo_no},    #{board_type},    #{bo_title},
    #{bo_writer},    SYSDATE,    #{bo_content},
    #{bo_pass},    #{bo_ip}    

    ,  #{bo_parent}

)

mapper.update:
UPDATE BOARD2
SET
    bo_title = #{bo_title},
    bo_content = #{bo_content}
WHERE
    bo_no = #{bo_no}
