mapper.update:
UPDATE member SET MEM_NAME = #{mem_name} , MEM_BIR = #{mem_bir} , MEM_ZIP = #{mem_zip} , MEM_ADD1 = #{mem_add1} , MEM_ADD2 = #{mem_add2} , MEM_HOMETEL = #{mem_hometel} , MEM_COMTEL = #{mem_comtel} , MEM_HP = #{mem_hp} , MEM_MAIL = #{mem_mail} , MEM_JOB = #{mem_job} , MEM_LIKE = #{mem_like} , MEM_MEMORIAL = #{mem_memorial} , MEM_MEMORIALDAY = #{mem_memorialday}, MEM_MILEAGE = #{mem_mileage}
, MEM_IMG = #{mem_img,jdbcType=BLOB}
WHERE MEM_ID = #{mem_id}
